<?php $getu = $con->prepare('SELECT fullname, email, sub, initials, color FROM customers INNER JOIN customers__card__subscription ON customers__card__subscription.uid = customers.id INNER JOIN customers__header ON customers__header.uid = customers.id WHERE customers.id = ?'); $getu->bind_param('i', $guid); $getu->execute(); $getu->bind_result($gname, $gemail, $gsub, $ginitials, $gcolor); $getu->fetch(); $getu->close(); ?>