        /*
        SELECT 
            pid,
            ((
                (5 * (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 5)) + 
                (4 * (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 4)) + 
                (3 * (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 3)) + 
                (2 * (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 2)) + 
                (1 * (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 1))
            ) / 
            (
                (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 5) +
                (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 4) +
                (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 3) +
                (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 2) +
                (SELECT COUNT(stars) FROM reviews WHERE pid = 1 AND stars = 1)
            )) AS 'avg'
        FROM `reviews` WHERE pid = 1 GROUP BY pid
        */
        $pidarr = array(); $avgArr = array();
        $sql = "SELECT DISTINCT model FROM products__search INNER JOIN products__variations ON products__variations.pid = products__search.pid INNER JOIN products__category ON products__category.pid = products__search.pid INNER JOIN products__status ON products__status.pid = products__search.pid INNER JOIN products__inventory ON products__inventory.pid = products__search.pid INNER JOIN products__shipping ON products__shipping.pid = products__search.pid WHERE MATCH (products__search.name, products__search.category, products__search.tags, products__search.brand) AGAINST ('$term') OR products__search.name LIKE '%$term%' OR products__search.category LIKE '%$term%' OR products__search.tags LIKE '%$term%' OR products__search.brand LIKE '%$term%' AND products__status.status = 1";
        $res = $con->query($sql);
        while ($dt = $res->fetch_assoc()) { $model = $dt['model'];
            $isql = "SELECT products.id, products.name, products.description, products.thumbnail, products__inventory.quantity, products__inventory.code, products__variations.color, products__variations.brand, products__pricing.base, products__pricing.discounted, products__pricing.discount FROM products INNER JOIN products__inventory ON products__inventory.pid = products.id INNER JOIN products__variations ON products__variations.pid = products.id INNER JOIN products__pricing ON products__pricing.pid = products.id WHERE products__variations.model LIKE '$model' LIMIT 1";
            $ires = $con->query($isql);
            while ($dta = $ires->fetch_assoc()) { $rpid = $dta['id'];
                $fvstr = 0; $fostr = 0; $thstr = 0; $twstr = 0; $onstr = 0; $du = 0; $dd = 0;
                $rsql = "SELECT stars, pid FROM reviews WHERE pid = $rpid"; $rres = $con->query($rsql);
                while ($rv = $rres->fetch_assoc()) {
                    if ($rv['stars'] == 5) { $fvstr++; } if ($rv['stars'] == 4) { $fostr++; } if ($rv['stars'] == 3) { $thstr++; } if ($rv['stars'] == 2) { $twstr++; } if ($rv['stars'] == 1) { $onstr++; }
                    $du = ((5 * $fvstr) + (4 * $fostr) + (3 * $thstr) + (2 * $twstr) + (1 * $onstr)); $dd = ($fvstr + $fostr + $thstr + $twstr + $onstr); $avg = $du / $dd;
                    $spid = $rv['pid']; if (!in_array($spid, $pidarr)) { array_push($pidarr, $spid); }
                }
            }
        }
        for ($i = 0; $i < sizeof($pidarr); $i++) {
            $gspid = $pidarr[$i];
            $revsql = "SELECT pid,
                ROUND(
                    (
                        (5 * (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 5)) + 
                        (4 * (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 4)) + 
                        (3 * (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 3)) + 
                        (2 * (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 2)) + 
                        (1 * (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 1))
                    ) / 
                    (
                        (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 5) +
                        (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 4) +
                        (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 3) +
                        (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 2) +
                        (SELECT COUNT(stars) FROM reviews WHERE pid = $gspid AND stars = 1)
                    )
                ) AS 'avg'
            FROM `reviews` WHERE pid = $gspid GROUP BY pid"; $revres = $con->query($revsql);
            while ($rev = $revres->fetch_assoc()) { array_push($avgArr, $rev['avg']); }
        } $cfvs = 0; $cfos = 0; $cths = 0; $ctws = 0; $cons = 0;
        for ($i = 0; $i < sizeof($avgArr); $i++) {
            if ($avgArr[$i] == 5) { $cfvs++; }
            if ($avgArr[$i] == 4) { $cfos++; }
            if ($avgArr[$i] == 3) { $cths++; }
            if ($avgArr[$i] == 2) { $ctws++; }
            if ($avgArr[$i] == 1) { $cons++; }
        }
