        }
        if (price) { bfData.set('mxi', price); mxislider.noUiSlider.set([price.split('+')[0], price.split('+')[1]]); }
        if (refund) { bfData.set('refund', refund); document.getElementById('categ-refund-yes').checked = true; } else {  bfData.delete('refund'); }
        if (backorder) { bfData.set('backorder', backorder); document.getElementById('categ-backord-yes').checked = true; } else {  bfData.delete('backorder'); }
        __filteritems(bfData);
    } __getfilters();

    function __setbrands(brand) { var gbi = '';
        if (brand.length > 0) {
            for (let i = 0; i < brand.length; i++) { gbi += brand[i]; if (i !== gbi.length - 1) { gbi += '+'; } } var cgbi = gbi.slice(0, -1);
            if (queryParams.has('fdbd')) { queryParams.delete('fdbd'); queryParams.set("fdbd", cgbi); }
            else { queryParams.set("fdbd", cgbi); } history.replaceState(null, null, "?"+queryParams.toString());
        } else { queryParams.delete('fdbd'); history.replaceState(null, null, "?"+queryParams.toString()); }
    }
    function __setreviews (review) { var crbi = review.join('+');
        if (review.length > 0) { bfData.set('review', review);
            if (queryParams.has('review')) { queryParams.delete('review'); queryParams.set('review', crbi); }
            else { queryParams.set('review', crbi) } history.replaceState(null, null, "?"+queryParams.toString());
        } else { queryParams.delete('review'); bfData.delete('review'); history.replaceState(null, null, "?"+queryParams.toString()); }
        __getfilters();
    }
    function __setprice (mxi) { queryParams.set("mxi", mxi); history.replaceState(null, null, "?"+queryParams.toString()); }
