    function __setbackorder (backorder) { if (backorder == 1) { queryParams.set("bko", backorder); } else { queryParams.delete("bko");  bfData.delete('backorder'); } history.replaceState(null, null, "?"+queryParams.toString()); __getfilters(); }

    var ctbrd = document.getElementsByClassName('categ-brand'); var brndar = [];
    if (queryParams.has('fdbd')) { var gb = queryParams.get('fdbd').split('+'); for (let i = 0; i < gb.length; i++) { brndar.push (gb[i]); } }
    for (let i = 0; i < ctbrd.length; i++) {
        ctbrd[i].addEventListener('click', () => {
            if (ctbrd[i].checked) {
                if (bfData.get('brand')) {
                    for (const pair of bfData.entries()) {
                        if (pair[0] == 'brand' && !pair[1].split(',').includes(ctbrd[i].value)) { bfData.set("brand", bfData.get('brand')+','+ctbrd[i].value); }
                    }
                } else { bfData.set('brand', ctbrd[i].value); }
                if (brndar.includes(ctbrd[i].value) === false) { brndar.push(ctbrd[i].value); } __setbrands(brndar);
            } else { var bitm = bfData.get('brand');
                for (const pair of bfData.entries()) {
                    if (pair[1].split(',').includes(ctbrd[i].value)) { var nbt = bitm.replace(ctbrd[i].value, '');
                        if (nbt.length > 0) {
                            if (nbt.replace(/,(?=,)/, '')) { var nsbt = nbt.replace(/,(?=,)/, ''); }
                            if (nbt[nbt.length-1] == ',') { var nsbt = nbt.slice(0, -1); }
                            if (nbt[0] == ',') { var nsbt = nbt.substring(1); }
                            if (nsbt) { bfData.set("brand", nsbt); }
                            else { bfData.set("brand", nbt); }
                        } else { bfData.delete("brand"); }
                    }
                }
                if (brndar.indexOf(ctbrd[i].value) !== -1) { brndar.splice(brndar.indexOf(ctbrd[i].value), 1); }
                __setbrands(brndar);
            } __getfilters();
        });
    }

    var revbtn = document.getElementsByClassName('categ-review'); var revArr = [];
    if (queryParams.has('review')) { var rv = queryParams.get('review').split('+'); for (let i = 0; i < rv.length; i++) { revArr.push(rv[i]); } }
